fetch("{{site.baseurl}}/test/ref/text.zip").then(function(response){if(response.status===200||response.status===0){return Promise.resolve(response.blob())}else{return Promise.reject(new Error(response.statusText))}}).then(JSZip.loadAsync).then(function(zip){return zip.file("Hello.txt").async("string")}).then(function success(text){$("#fetch").append($("<p>",{class:"alert alert-success",text:"loaded, content = "+text}))},function error(e){$("#fetch").append($("<p>",{class:"alert alert-danger",text:e}))});