var promise=new JSZip.external.Promise(function(resolve,reject){JSZipUtils.getBinaryContent("{{site.baseurl}}/test/ref/text.zip",function(err,data){if(err){reject(err)}else{resolve(data)}})});promise.then(JSZip.loadAsync).then(function(zip){return zip.file("Hello.txt").async("string")}).then(function success(text){$("#jszip_utils").append($("<p>",{class:"alert alert-success",text:"loaded, content = "+text}))},function error(e){$("#jszip_utils").append($("<p>",{class:"alert alert-danger",text:e}))});