var $result=$("#result");$("#file").on("change",function(evt){$result.html("");$("#result_block").removeClass("hidden").addClass("show");function handleFile(f){var $title=$("<h4>",{text:f.name});var $fileContent=$("<ul>");$result.append($title);$result.append($fileContent);var dateBefore=new Date;JSZip.loadAsync(f).then(function(zip){var dateAfter=new Date;$title.append($("<span>",{class:"small",text:" (loaded in "+(dateAfter-dateBefore)+"ms)"}));zip.forEach(function(relativePath,zipEntry){$fileContent.append($("<li>",{text:zipEntry.name}))})},function(e){$result.append($("<div>",{class:"alert alert-danger",text:"Error reading "+f.name+": "+e.message}))})}var files=evt.target.files;for(var i=0;i<files.length;i++){handleFile(files[i])}});